<template>
<div class="tree-box">
  <div class="box" @click="foldEvent()">
    <div class="left" :class="{open: data.info.fold, close: !data.info.fold && !data.info.file}"></div>
    <div class="right-box">
      <div class="center">
        <span class="icon-folder" v-if="!data.info.file"></span>
        <span class="icon-file" v-if="data.info.file"></span>
        <span class="text">{{data.info.text}}</span>
      </div>
    </div>
  </div>
  <div v-show="data.info.fold">
    <ehr-tree-content :data="data.tree"></ehr-tree-content>
  </div>
</div>
</template>

<script>
export default {
  name: 'tree',
  props: ['data'],
  data () {
    return {};
  },
  methods: {
    ajax (type, url, data, fun) {
      type = type.toUpperCase();
      var xhr = new XMLHttpRequest();
      xhr.onreadystatechange = function() {
        if (xhr.readyState == 4 && xhr.status == 200) {
          if (fun) fun(xhr.response);
        }
      }
      xhr.open(type, url, true);
      xhr.setRequestHeader("Authorization", 'token 74291eabf84c59ea403883eafe6e15e88697170a');
      xhr.setRequestHeader("X-PJAX", 'true');
      xhr.setRequestHeader("X-PJAX-Container", '#js-repo-pjax-container, .context-loader-container, [data-pjax-container]');
      xhr.setRequestHeader("X-Requested-With", 'XMLHttpRequest');
      xhr.setRequestHeader("Content-Type", 'application/x-www-form-urlencoded; charset=UTF-8');
      xhr.send(data);
    },
    foldEvent () {
      if (this.data.info.file) {
        this.ajax('get', this.data.info.aurl, {_pjax: '#js-repo-pjax-container, .context-loader-container, [data-pjax-container]'}, (res) => {
          console.log(history);
          document.getElementById('js-repo-pjax-container').innerHTML = res;
        });
      }
      else this.data.info.fold = !this.data.info.fold;
    }
  }
}
</script>
