<template>
  <nav id="contentDom" :class="{'open': openStatus}">
      <div class="header">
        <div class="left">
          <div class="master">
            <a :href="'https://github.com/' + object.username">{{object.username}}</a> /
            <span @click="masterEvent()"><a style="cursor: pointer">{{object.repository}}</a></span>
          </div>
          <div class="branch">master</div>
        </div>
        <div class="right">
          <div class="status-box" :class="{'open-btn': !openStatus}" @click="openStatusEvent()">
            <div class="status" :class="{'refresh': loading, 'toward': !loading && !openStatus, 'goback': !loading && openStatus}"></div>
          </div>
        </div>
      </div>
      <div class="ul" style="height: calc(100% - 54px); overflow: auto;">
          <!-- <a href="###">{{item.text}}</a> -->
          <div class="error-tip" v-show="errorTipStatus">
            令牌无效。按照 <a target="_blank" href="https://github.com/settings/tokens/new">此链接</a> 创建一个新令牌并将其粘贴到下面。
            <br/><br/>
            <input type="text" v-model="token1"/>
            <button @click="saveClickEvent(token1)">保存</button>
          </div>
          <template v-for="item in arr">
            <ehr-tree :data="item"></ehr-tree>
          </template>
      </div>
  </nav>
</template>
<script>
  import tree from './lib/tree.js';
  import {mapState, mapMutations, mapActions} from 'vuex';
  export default {
    data () {
      return {
        object: this.master,
        arr: [],
        token1: ''
      };
    },
    computed: {
      ...mapState(['openStatus', 'loading', 'errorTipStatus'])
    },
    watch: {
      openStatus () {
        this.loadingEvent(false);
        if (!this.arr.length && this.openStatus) this.ajaxFunc();
      }
    },
    created () {
      console.log('this.object: ', this.object);
      if (this.openStatus) this.ajaxFunc();
    },
    methods: {
      ...mapMutations(['saveEvent', 'openStatusEvent', 'loadingEvent']),
      ...mapActions(['ajax', 'pjax']),
      masterEvent () {
        this.pjax({
          type: 'get',
          url: '/' + this.object.username + '/' + this.object.repository,
          data: {_pjax: '#js-repo-pjax-container, .context-loader-container, [data-pjax-container]'},
          fun: (res) => {
            document.getElementById('js-repo-pjax-container').innerHTML = res;
          },
          _this: this
        });
      },
      saveClickEvent (token1) {
        this.saveEvent(token1);
        setTimeout(() => {
          this.ajaxFunc();
        }, 100);
      },
      ajaxFunc () {
        console.log('ajaxFunc');
        this.ajax({
          type: 'get',
          url: this.object.url,
          fun: (res) => {
            this.arr = this.sort(tree(res.tree, this.object));
          },
          _this: this
        });
      },
      sort (arr) {
        return arr.sort();
      }
    }
  };
</script>
<style lang="less" scoped>
  #contentDom {
    width: 240px;
    height: 100%;
    background: #fff;
    border-right: 1px solid #e1e4e8;
    position: fixed;
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol";
    top: 0;
    left: -240px;
    transition: all .3s;
    &.open {
      left: 0;
    }
    .header {
      width: 100%;
      height: 54px;
      border-bottom: 1px solid #e1e4e8;
      padding: 5px 10px;
      box-sizing: border-box;
      font-size: 14px;
      line-height: 20px;
      color: #0366d6;
      display: flex;

      .left {
        flex: 1;
        .master {
          height: 20px;
          overflow: hidden;
        }
        .branch {
          color: #444;
        }
      }
      .right {
        width: 30px;
        position: relative;
        .status-box {
          position: absolute;
          top: 5px;
          width: 30px;
          height: 30px;
          background: #fefefe;
          border: 1px solid #aaa;
          border-radius: 5px;
          left: 0;
          transition: all .3s;
          cursor: pointer;
          &:hover {
            background: #eee;
          }

          &.open-btn {
            left: 50px;
          }
        }
        .status {
          width: 100%;
          height: 100%;
          transition: all .3s;
          &.refresh {
            animation: an 1s linear 0s infinite normal;
            background: url("data:image/png;base64,") no-repeat center center;
            background-size: 20px;
          }
          &.toward {
            background:  url("data:image/png;base64,") no-repeat center center;
            background-size: 20px;
          }
          &.goback {
            background:  url("data:image/png;base64,") no-repeat center center;
            background-size: 20px;
          }
        }
      }
    }
    .ul {
      overflow-y: scroll;
      padding: 0;
      margin: 0;
      .error-tip {
        padding: 10px 15px;
        box-sizing: border-box;
        font-size: 13px;
        color: #444;
      }
      li {
        a {
          font-size: 12px;
          line-height: 28px;
          display: block;
          color: #0366d6;
          text-decoration: none;
          &:hover {
            background: #eee;
            color: #333;
          }
        }
      }
    }
  }

  @keyframes an {
    from { transform: rotate(0deg); }
    to { transform: rotate(360deg); }
  }
</style>
